// El resto del código de js/products.js (funciones de display, filtro, paginación, etc.) permanece igual
document.addEventListener('DOMContentLoaded', function() {
    // --- Modal Elements ---
    const modal = document.getElementById('quantity-modal');
    const modalProductName = document.getElementById('modal-product-name');
    const modalQuantityInput = document.getElementById('modal-quantity-input');
    const modalConfirmBtn = document.getElementById('modal-confirm-btn');
    const modalCancelBtn = document.getElementById('modal-cancel-btn');
    let currentProductForModal = null;

    // --- Modal Functions ---
    function openModal(product) {
        currentProductForModal = product;
        modalProductName.textContent = product.name;
        modalQuantityInput.value = '1';
        modal.style.display = 'flex';
        modalQuantityInput.focus();
    }

    function closeModal() {
        modal.style.display = 'none';
        currentProductForModal = null;
    }

    function confirmQuantity() {
        const quantity = parseInt(modalQuantityInput.value);
        if (currentProductForModal && !isNaN(quantity) && quantity > 0) {
            if (typeof addItemToCart === "function") {
                addItemToCart(currentProductForModal, quantity);
                closeModal();
            } else {
                console.error("addItemToCart function not found.");
            }
        } else {
            alert("Por favor, ingrese un número válido de unidades.");
        }
    }

    // --- Event Listeners for Modal ---
    if (modal) {
        modalCancelBtn.addEventListener('click', closeModal);
        modalConfirmBtn.addEventListener('click', confirmQuantity);
        modal.addEventListener('click', function(e) {
            if (e.target === modal) {
                closeModal();
            }
        });
        modalQuantityInput.addEventListener('keydown', function(e) {
            if (e.key === 'Enter') {
                confirmQuantity();
            }
        });
    }


    console.log("[DEBUG] DOMContentLoaded: sampleProducts.length =", sampleProducts.length); // LOG 2
    const productGrid = document.getElementById('product-grid');
    const categoryList = document.getElementById('category-list');
    const productListTitle = document.getElementById('product-list-title');
    const paginationControlsContainer = document.getElementById('pagination-controls');

    const categories = definedCategories;

    if (categoryList) {
        categoryList.innerHTML = '';
        const allLi = document.createElement('li');
        allLi.textContent = "Todas las categorías";
        allLi.dataset.category = "all";
        allLi.addEventListener('click', () => filterProducts("all"));
        categoryList.appendChild(allLi);

        categories.forEach(category => {
            const li = document.createElement('li');
            li.textContent = category;
            li.dataset.category = category;
            li.addEventListener('click', () => filterProducts(category));
            categoryList.appendChild(li);
        });
    }

    // Variables para paginación
    let currentPage = 1;
    const itemsPerPage = 50;
    let currentFilteredProducts = [];

    function renderAdvancedPagination(totalItems, page, itemsPerPage) {
        if (!paginationControlsContainer) return;
        paginationControlsContainer.innerHTML = '';
        const totalPages = Math.ceil(totalItems / itemsPerPage);

        if (totalPages <= 1) {
            return;
        }

        const createButton = (text, pageNum, isDisabled = false, isEllipsis = false, isActive = false) => {
            const button = document.createElement('button');
            button.textContent = text;
            button.classList.add('pagination-button');
            if (isActive) {
                button.classList.add('active');
            }
            if (isEllipsis) {
                button.classList.add('ellipsis');
                button.disabled = true;
            } else {
                button.dataset.page = pageNum;
                if (isDisabled) {
                    button.disabled = true;
                }
                button.addEventListener('click', () => {
                    displayProducts(currentFilteredProducts, pageNum);
                    window.scrollTo(0, 0); // Scroll to top for better UX
                });
            }
            return button;
        };

        // Botones "Inicio" y "Anterior"
        paginationControlsContainer.appendChild(createButton('Inicio', 1, page === 1));
        paginationControlsContainer.appendChild(createButton('Anterior', page - 1, page === 1));

        const range = 2; // Número de páginas a mostrar a cada lado de la actual
        let pages = [];

        if (totalPages <= (range * 2) + 3) { // Si no hay suficientes páginas para necesitar elipsis
            for (let i = 1; i <= totalPages; i++) {
                pages.push(i);
            }
        } else {
            pages.push(1); // Siempre mostrar la primera página
            if (page > range + 2) {
                pages.push('...');
            }

            let start = Math.max(2, page - range);
            let end = Math.min(totalPages - 1, page + range);

            if (page <= range + 1) {
                end = 1 + (range * 2);
            }

            if (page >= totalPages - range) {
                start = totalPages - (range * 2);
            }

            for (let i = start; i <= end; i++) {
                pages.push(i);
            }

            if (page < totalPages - range - 1) {
                pages.push('...');
            }

            pages.push(totalPages); // Siempre mostrar la última página
        }

        // Eliminar duplicados y renderizar
        const uniquePages = [...new Set(pages)];
        uniquePages.forEach(p => {
            if (p === '...') {
                paginationControlsContainer.appendChild(createButton('...', 0, false, true));
            } else {
                paginationControlsContainer.appendChild(createButton(p, p, false, false, p === page));
            }
        });


        // Botones "Siguiente" y "Fin"
        paginationControlsContainer.appendChild(createButton('Siguiente', page + 1, page === totalPages));
        paginationControlsContainer.appendChild(createButton('Fin', totalPages, page === totalPages));
    }

    function displayProducts(productsToDisplay, page = 1) {
        console.log(`[DEBUG] displayProducts: productsToDisplay.length=${productsToDisplay.length}, page=${page}`); // LOG 3
        if (!productGrid) {
            // console.error("[DEBUG] productGrid not found!");
            return;
        }
        productGrid.innerHTML = '';
        currentPage = page;

        const startIndex = (currentPage - 1) * itemsPerPage;
        const endIndex = startIndex + itemsPerPage;
        const paginatedItems = productsToDisplay.slice(startIndex, endIndex);
        // console.log(`[DEBUG] displayProducts: startIndex=${startIndex}, endIndex=${endIndex}, paginatedItems.length=${paginatedItems.length}`);

        const noResultsMessage = document.getElementById('no-results-message');

        if (productsToDisplay.length === 0) {
            if (noResultsMessage) noResultsMessage.style.display = 'block';
            if (paginationControlsContainer) paginationControlsContainer.innerHTML = '';
        } else {
            if (noResultsMessage) noResultsMessage.style.display = 'none';
            if (paginatedItems.length === 0 && page > 1) {
                 console.warn(`[DEBUG] displayProducts: No items to display on page ${page}, but productsToDisplay is not empty. Might be an invalid page number for current filter.`);
            }
            paginatedItems.forEach(product => {
                const productItem = document.createElement('div');
                productItem.classList.add('product-item');
                let productImage = product.image || 'images/placeholder_general.png';
                if (!product.image && product.category) {
                    const categorySlug = product.category.toLowerCase().replace(/\s+/g, '_').replace(/[^a-z0-9_]/gi, '');
                    productImage = `images/placeholder_cat_${categorySlug}.png`;
                }
                productItem.innerHTML = `
                    <img src="${productImage}" alt="${product.name}" onerror="this.onerror=null;this.src='images/placeholder_general.png';">
                    <h4>${product.name}</h4>
                    <p class="price">S/${product.price.toFixed(2)}</p>
                    <div class="buttons-container">
                        <button class="view-detail-btn btn-secondary" data-product-id="${product.id}">Ver Detalle</button>
                        <button class="add-to-cart-btn-grid btn" data-product-id="${product.id}">Añadir al Carrito</button>
                    </div>
                `;
                productGrid.appendChild(productItem);
            });
            renderAdvancedPagination(productsToDisplay.length, currentPage, itemsPerPage);
        }
        addEventListenersToProductButtons();
    }

    function filterProducts(category) {
        console.log("[DEBUG] filterProducts called with category =", category); // LOG 4
        const productListTitle = document.getElementById('product-list-title');
        document.querySelectorAll('#category-list li').forEach(li => li.classList.remove('active-category'));
        const currentCategoryLi = document.querySelector(`#category-list li[data-category="${category}"]`);
        if (currentCategoryLi) currentCategoryLi.classList.add('active-category');

        if (category === "all") {
            currentFilteredProducts = [...sampleProducts];
            if(productListTitle) productListTitle.textContent = "Todos los Productos";
        } else {
            currentFilteredProducts = sampleProducts.filter(product => product.category === category);
            if(productListTitle) productListTitle.textContent = category;
        }
        console.log(`[DEBUG] filterProducts: currentFilteredProducts.length = ${currentFilteredProducts.length}`);
        displayProducts(currentFilteredProducts, 1);

        const searchInputs = document.querySelectorAll('.search-input-header');
        searchInputs.forEach(input => input.value = '');
        const currentUrl = new URL(window.location);
        currentUrl.searchParams.delete('search');
        if (category === "all") {
            currentUrl.hash = '';
        } else {
            currentUrl.hash = encodeURIComponent(category);
        }
        window.history.pushState({path:currentUrl.href}, '', currentUrl.href);
    }

    function performSearch(query) {
        console.log("[DEBUG] performSearch called with query =", query); // LOG 5
        const productListTitle = document.getElementById('product-list-title');
        const searchTerm = query.toLowerCase().trim();
        document.querySelectorAll('#category-list li').forEach(li => li.classList.remove('active-category'));

        if (!searchTerm) {
            currentFilteredProducts = [...sampleProducts];
            if(productListTitle) productListTitle.textContent = "Todos los Productos";
            const allLi = document.querySelector('#category-list li[data-category="all"]');
            if (allLi) allLi.classList.add('active-category');
        } else {
            currentFilteredProducts = sampleProducts.filter(product => {
                return product.name.toLowerCase().includes(searchTerm) ||
                       (product.description && product.description.toLowerCase().includes(searchTerm)) ||
                       (product.category && product.category.toLowerCase().includes(searchTerm));
            });
            if (productListTitle) {
                productListTitle.textContent = `Resultados para: "${query}" (${currentFilteredProducts.length})`;
            }
        }
        console.log(`[DEBUG] performSearch: currentFilteredProducts.length = ${currentFilteredProducts.length}`);
        displayProducts(currentFilteredProducts, 1);
    }

    function addEventListenersToProductButtons() {
        const viewDetailButtons = document.querySelectorAll('.view-detail-btn');
        viewDetailButtons.forEach(button => {
            button.replaceWith(button.cloneNode(true));
        });
        document.querySelectorAll('.view-detail-btn').forEach(button => {
            button.addEventListener('click', function() {
                const productId = this.dataset.productId;
                window.location.href = `product-detail.html?id=${productId}`;
            });
        });

        const addToCartGridButtons = document.querySelectorAll('.add-to-cart-btn-grid');
        addToCartGridButtons.forEach(button => {
            button.replaceWith(button.cloneNode(true));
        });
        document.querySelectorAll('.add-to-cart-btn-grid').forEach(button => {
            button.textContent = 'Añadir a Cotización'; // Update button text
            button.addEventListener('click', function() {
                const productId = parseInt(this.dataset.productId, 10);
                const product = getProductById(productId); // Use getProductById
                if (product) {
                    openModal(product);
                } else {
                    console.error("Product not found for ID:", productId);
                }
            });
        });
    }

    if (productGrid) {
        const urlParams = new URLSearchParams(window.location.search);
        const searchQuery = urlParams.get('search');
        const hashCategory = decodeURIComponent(window.location.hash.substring(1));
        const categoriesForCheck = definedCategories;

        if (searchQuery) {
            console.log("[DEBUG] Initial load: Search query detected =", searchQuery);
            performSearch(searchQuery);
            const searchInputOnProductsPage = document.getElementById('search-input-header-products');
            if (searchInputOnProductsPage) searchInputOnProductsPage.value = searchQuery;
        } else if (hashCategory && categoriesForCheck.includes(hashCategory)) {
            console.log("[DEBUG] Initial load: Hash category detected =", hashCategory);
            filterProducts(hashCategory);
        } else {
            console.log("[DEBUG] Initial load: Displaying all products.");
            currentFilteredProducts = [...sampleProducts];
            console.log("[DEBUG] Initial load: currentFilteredProducts.length =", currentFilteredProducts.length); // LOG 6
            displayProducts(currentFilteredProducts, 1);
            const allLi = document.querySelector('#category-list li[data-category="all"]');
            if (allLi) allLi.classList.add('active-category');
            if(productListTitle) productListTitle.textContent = "Todos los Productos";
        }
    }

    const searchFormOnProductsPage = document.getElementById('search-form-header-products');
    const searchInputOnProductsPage = document.getElementById('search-input-header-products');

    if (searchFormOnProductsPage && searchInputOnProductsPage) {
         searchFormOnProductsPage.addEventListener('submit', function(e) {
            e.preventDefault();
            const searchTerm = searchInputOnProductsPage.value;
            const currentUrl = new URL(window.location);
            currentUrl.searchParams.set('search', searchTerm);
            currentUrl.hash = '';
            window.history.pushState({path:currentUrl.href}, '', currentUrl.href);
            performSearch(searchTerm);
        });
        searchInputOnProductsPage.addEventListener('input', function() {
            if (this.value.trim() === '') {
                 const currentUrl = new URL(window.location);
                 currentUrl.searchParams.delete('search');
                 window.history.pushState({path:currentUrl.href}, '', currentUrl.href);
                 performSearch('');
            }
        });
    }

    if (window.location.pathname.endsWith('product-detail.html')) {
        const params = new URLSearchParams(window.location.search);
        const productId = parseInt(params.get('id'));
        const product = sampleProducts.find(p => p.id === productId);

        const productDetailContainer = document.getElementById('product-detail-content');
        const breadcrumbProductName = document.getElementById('breadcrumb-product-name');

        if (product && productDetailContainer) {
            document.title = `${product.name} - Ferretería Online`;
            if (breadcrumbProductName) breadcrumbProductName.textContent = product.name;

            const imageContainer = productDetailContainer.querySelector('.product-image-container');
            if (imageContainer) {
                let productImage = product.image || `images/placeholder_general.png`;
                 if (!product.image && product.category) {
                    const categorySlug = product.category.toLowerCase().replace(/\s+/g, '_').replace(/[^a-z0-9_]/gi, '');
                    productImage = `images/placeholder_cat_${categorySlug}.png`;
                }
                imageContainer.innerHTML = `<img src="${productImage}" alt="${product.name}" onerror="this.onerror=null;this.src='images/placeholder_general.png';">`;
            }

            const infoContainer = productDetailContainer.querySelector('.product-info-container');
            if (infoContainer) {
                infoContainer.querySelector('h2').textContent = product.name;
                infoContainer.querySelector('.price-detail').textContent = `S/${product.price.toFixed(2)}`;
                const descriptionEl = infoContainer.querySelector('.price-detail + p');
                if(descriptionEl) descriptionEl.textContent = product.description;

                const categorySpan = document.getElementById('product-category-detail');
                if(categorySpan) categorySpan.textContent = product.category;

                const addToCartBtnDetail = document.getElementById('add-to-cart-btn-detail');
                if (addToCartBtnDetail) {
                    addToCartBtnDetail.dataset.productId = product.id;

                    const newAddToCartBtnDetail = addToCartBtnDetail.cloneNode(true);
                    addToCartBtnDetail.parentNode.replaceChild(newAddToCartBtnDetail, addToCartBtnDetail);

                    newAddToCartBtnDetail.addEventListener('click', function() {
                        const product = getProductById(this.dataset.productId);
                        if (product) {
                            openModal(product);
                        }
                    });
                }
            }
        } else if (productDetailContainer) {
            productDetailContainer.innerHTML = "<p>Producto no encontrado. <a href='products.html' class='btn'>Volver al catálogo</a>.</p>";
            if (breadcrumbProductName) breadcrumbProductName.textContent = "Producto no encontrado";
        }
    }
});

function getProductById(id) {
    // Ensure sampleProducts is available or fetched before calling this
    return sampleProducts.find(product => product.id === parseInt(id));
}
